<script>
import Datepicker from 'vuejs-datepicker'
import draggable from 'vuedraggable'
export default {
  components: {
    Datepicker,
    draggable
  },
  name: 'SampleDiv',
  data () {
    return {
      msg: 'Sample Div',
      tasks: [],
      taskId: 0,
      dragging: false,
      displayModel: false,
      taskData: '',
      taskDate: new Date(),
      days: ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'],
      months: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
      dragIndex: 0
    }
  },
  methods: {
    addTask () {
      console.log('called add task')
      this.tasks.push({
        id: this.taskId,
        text: 'Task ' + (this.taskId + 1)
      })

      this.taskId++
    },
    addTask2 () {
      console.log('called add task2 ', this.taskDate)
      this.tasks.push({
        id: this.taskId,
        text: this.taskData,
        date: this.taskDate
      })
      this.taskData = ''
      this.displayModel = false
      this.taskId++
    },
    drag_start (event) {
      this.deleteIcon = true
      console.log(' in drag start handler')
      // event.dataTransfer.setData('index', this.dragIndex)
    },
    drag_over (event) {
      console.log(' in drag over handler')
      event.preventDefault()
    },
    drop (event) {
      console.log('in drop ', event)
      event.preventDefault()
      let index = this.dragIndex
      console.log('index ', index, this.dragIndex)
      this.tasks.splice(index, 1)
      this.dragging = false
      // this.deleteIcon = false
    },
    drag_end () {
      this.deleteIcon = false
    },
    openModel () {
      this.displayModel = true
    },
    closeModel () {
      this.displayModel = false
    },
    doSomethingInParentComponentFunction () {
      console.log('doSomethingInParentComponentFunction called')
    },
    datepickerOpenedFunction () {
      console.log('datepickerOpenedFunction called')
    },
    datepickerClosedFunction () {
      console.log('datepickerClosedFunction called')
    },
    frontEndDateFormat (date) {
      let nDate = new Date(date)
      return this.days[nDate.getDay()] + ' ' + this.months[nDate.getMonth()] + ' ' + nDate.getDate()
    }
  }
}
</script>

